function changeSearchSelect(type){

    if(type=="all"){
        $("#td_key").html('<input id="input_search1" style="border: 1px solid #ddd;" title="" class="yssp " placeholder="关键字"name="keywords" th:value="${keywords}" value="" type="text"/>');

    }else if(type=="payed"){
        $("#td_key").html('<select id="select_search2" name="keywords" class="yssp" style=" height:29px;padding: 0px 90px 0px 4px; border:1px solid #ddd; vertical-align:middle"> <option value="1">是</option> <option value="0">否</option> </select>');

    }else if(type=="passed"){
        $("#td_key").html('<select id="select_search3" name="keywords" class="yssp" style=" height:29px;padding: 0px 90px 0px 4px; border:1px solid #ddd; vertical-align:middle"> <option value="1">通过考试</option> <option value="2">未通过</option> <option value="0">未考试</option></select>');

    }else if(type=="certificateStatus"){
        $("#td_key").html('<select id="select_search4" name="keywords" class="yssp" style=" height:29px;padding: 0px 90px 0px 4px; border:1px solid #ddd; vertical-align:middle"> <option value="2">申请中</option><option value="1">已领证</option> <option value="0">未申请</option> </select>');

    }
}


